METHOD.ACTIONSCRIPT:
#EACH()
SELECT:CL CF.CLASSIFICACAO_FIS AS NCM , DP.CLASS_PROD FROM PMZ_DE_PARA_NCM_CLASS_PROD DP
     INNER JOIN CLASSIFICACAO_FIS CF ON (DP.NCM = CF.DESCRICAO)
//WHERE DP.NCM='15180090'

   #BEGIN
     INSERT INTO PMZ_CLASSIFICACAO_FIS (CLASSIFICACAO_FIS,CLASSIFICACAO_PRODUTO) VALUES (:CL.NCM, :CL.CLASS_PROD);
   #END;
