//Prepara os registros das condicoes de pagamento SELECT:COND_PAGTO cond.condicoes_pgto AS ITEM FROM #REPLACE(TBCONDPAGTO) L INNER JOIN condicoes_pgto cond ON cond.codigo=L.COD_CONDICAO_PAGAMENTO;
//Busca ID das contas contabeis
SELECT:CTB ( SELECT PCONTA FROM PLANO_CONTAS WHERE CODIGO =:CC_DEBITO) AS CC_DEBITO, ( SELECT PCONTA FROM PLANO_CONTAS WHERE CODIGO =:CC_CREDITO) AS CC_CREDITO, ( SELECT PCONTA FROM PLANO_CONTAS WHERE CODIGO =:CC_ADIANTAMENTO) AS CC_ADIANTAMENTO FROM dual;
