METHOD.ACTIONSCRIPT:
#CHECK("SELECT COUNT(CODIGO) AS N FROM CONDICOES_PGTO WHERE CODIGO=:CODIGO AND CONDICOES_PGTO<>:CONDICOES_PGTO", N>0, const.COD_DUPL);

#call millenium.condicoes_pgto.validaparcelas( :PERCENTUAL, :DESCRICAO_PARC );

UPDATE CONDICOES_PGTO SET  CONDICOES_PGTO=:CONDICOES_PGTO, CODIGO=:CODIGO, DESCRICAO=:DESCRICAO, PRAZO=:PRAZO,
 TIPO_PGTO=:TIPO_PGTO, FIXA_MES30=:FIXA_MES30, MUDA_DATASEG=:MUDA_DATASEG, VALOR_MINIMO=:VALOR_MINIMO,
 IMPRIME_CARNE=:IMPRIME_CARNE, ENVIA_PDA=:ENVIA_PDA, TIPO=:TIPO, FIXA_DIA_CALCULO=:FIXA_DIA_CALCULO,
 CREDITO_OBRIGATORIO=:CREDITO_OBRIGATORIO, PAGAMENTO_FUTURO=:PAGAMENTO_FUTURO,
 NAO_IGUALAR_PARCELAS=:NAO_IGUALAR_PARCELAS, MUDA_DATAFERIADO=:MUDA_DATAFERIADO,
 TRUNCAR_PARCELAS=:TRUNCAR_PARCELAS, DESCONTO=:DESCONTO, TIPO_OPERACAO=:TIPO_OPERACAO,
 PERCENTUAL=:PERCENTUAL, LIMITE_MAX_DESC=:LIMITE_MAX_DESC, DESCRICAO_PARC=:DESCRICAO_PARC,
 TIPOS_PGTOS=:TIPOS_PGTOS, DIA_BASE=:DIA_BASE,
 PRAZOS_ANTECIPACAO_PV=:PRAZOS_ANTECIPACAO_PV
 WHERE (CONDICOES_PGTO=:CONDICOES_PGTO);

DELETE FROM CONDICOES_PGTO WHERE PRINCIPAL=:CONDICOES_PGTO;
DELETE FROM TIPOS_PGTOS_CONDICOES WHERE CONDICOES_PGTO=:CONDICOES_PGTO;
DELETE FROM CONDICOES_DIA_BASE WHERE CONDICOES_PGTO=:CONDICOES_PGTO;
DELETE FROM CONDICOES_PRAZO_ADIANT WHERE CONDICOES_PGTO=:CONDICOES_PGTO;

#EACH() DESCRICAO_PARC;
INSERT INTO CONDICOES_PGTO( PRAZO, PRINCIPAL, PERCENTUAL)
VALUES ( :DESCRICAO_PARC.PRAZO, :CONDICOES_PGTO, :DESCRICAO_PARC.PERCENTUAL)
#RETURN ( CONDICOES_PGTO AS CP);

#EACH() TIPOS_PGTOS;
INSERT INTO TIPOS_PGTOS_CONDICOES ( TIPO_PGTO, CONDICOES_PGTO, VALOR_MINIMO, ALT_VENC_OPERADOR, ALT_VENC_SUPERVISOR )
VALUES( :TIPOS_PGTOS.TIPO_PGTO, :CONDICOES_PGTO, :TIPOS_PGTOS.VALOR_MINIMO, :TIPOS_PGTOS.ALT_VENC_OPERADOR, :TIPOS_PGTOS.ALT_VENC_SUPERVISOR ) #RETURN(TIPOS_PGTOS_CONDICOES );

#EACH() DIA_BASE;
INSERT INTO CONDICOES_DIA_BASE ( CONDICOES_PGTO, DIA_FATURA, DIA_BASE )
VALUES ( :CONDICOES_PGTO, :DIA_BASE.DIA_FATURA, :DIA_BASE.DIA_BASE ) #RETURN(CONDICAO_DIA_BASE);

#EACH() PRAZOS_ANTECIPACAO_PV;
INSERT INTO CONDICOES_PRAZO_ADIANT( PRAZO, CONDICOES_PGTO, PERCENTUAL)
VALUES ( :PRAZOS_ANTECIPACAO_PV.PRAZO, :CONDICOES_PGTO, :PRAZOS_ANTECIPACAO_PV.PERCENTUAL) #RETURN ( CONDICAO_PRAZO_ADIANT );
