METHOD.ACTIONSCRIPT:
//API para baixa de titulo provisionados a PAGAR ou a RECEBER(Edson Vidal/Magdiel Araujo 30/06/2021)

//Consulta a conta informada
#PRIVATE()
  SELECT:CT CONTA FROM CONTAS WHERE NUMERO=:COD_CONTA ;
    #SELECT(CT._EOF, TRUE:{#CHECK("SELECT COUNT(*) AS N FROM DUAL",N>0,"Conta não encontrada !")},ELSE:{});

//Consulta o título
#PRIVATE()
  SELECT:L
    L.LANCAMENTO AS LANCAMENTO
  FROM LANCAMENTOS L
    INNER JOIN FILIAIS F ON F.FILIAL = L.FILIAL
  WHERE L.N_DOCUMENTO = :N_DOCUMENTO
    AND F.COD_FILIAL = :COD_FILIAL;
  #SELECT(L._EOF, TRUE:{#CHECK("SELECT COUNT(*) AS N FROM DUAL",N>0,"Titulo não encontrado !")},ELSE:{});

#CALL:BX MILLENIUM.TITULOS.BAIXA(
   LANCAMENTO=:L.LANCAMENTO
  ,VALOR=:VALOR
  ,DATA_PAGAMENTO=:DATA_PAGAMENTO
  ,GERA_OUTRO=:GERA_OUTRO
  ,OBSERVACAO=:OBSERVACAO
  ,CONTA=:CT.CONTA
);

//Retornando o ID da baixa
SELECT BAIXA FROM BAIXA WHERE BAIXA = :BX.BAIXA;
