METHOD.ACTIONSCRIPT:
//Método responsável pela atualização do saldo em estoque no ERP Millennium(Edson Vidal/Magdiel Araujo 10-06-2021)

#SET(TBITEM, ${#CREATETABLE(ID                 VARCHAR(50)
                           ,LOCAL              VARCHAR(50)
                           ,QUANTIDADE         DOUBLE PRECISION
                           )
});

#PRIVATE()
#EACH() PRODUTOS AS RS
    INSERT INTO #REPLACE(TBITEM) (
       ID
      ,LOCAL
      ,QUANTIDADE
    )
    VALUES (
       :RS.ID
      ,:RS.LOCAL
      ,:RS.QUANTIDADE
    );

#PRIVATE()
#EACH()
SELECT:SALDOS
   E.ID              AS BARRA
  ,E.LOCAL           AS LOCAL
  ,SUM(E.QUANTIDADE) AS QUANTIDADE
FROM #REPLACE(TBITEM) E
GROUP BY
  E.ID
 ,E.LOCAL

#CALL MILLENIUM_ECO.PRODUTOS.ATUALIZASALDODEESTOQUE(
  //VITRINE
   SALDOS=:SALDOS
  ,IGNORA_EMPENHO = TRUE
  ,COD_FILIAL =:COD_FILIAL
  ,IGNORA_PROD_SEM_CADASTRO = FALSE
  //,FILIAL
);
